    private void BotonCambiarSignoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BotonCambiarSignoActionPerformed
        if(!ResPantalla.getText().equals("")){
            if(esNumNegativo(ResPantalla.getText())){
                ResPantalla.setText(ResPantalla.getText().substring(1));
            }else{
                ResPantalla.setText("-"+ResPantalla.getText());
            }
        }else{
            ResPantalla.setText("-");
        }
    }//GEN-LAST:event_BotonCambiarSignoActionPerformed

    private void BotonDecimalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BotonDecimalActionPerformed
        if(ResPantalla.getText().length()<=0 || ResPantalla.getText().equals("-")){
            ResPantalla.setText(ResPantalla.getText()+"0.");
        }else if(!existePunto(ResPantalla.getText())){
            ResPantalla.setText(ResPantalla.getText()+".");
        }
    }//GEN-LAST:event_BotonDecimalActionPerformed

