    private void BotonBorrarNumeroActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BotonBorrarNumeroActionPerformed
        int i = ResPantalla.getText().length();
        int j = TxtPantalla.getText().length();
        TxtPantalla.setText(TxtPantalla.getText().substring(0, (j-i)));
        ResPantalla.setText("");
    }//GEN-LAST:event_BotonBorrarNumeroActionPerformed

    private void BotonInvertirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BotonInvertirActionPerformed
        double numero = Double.parseDouble(ResPantalla.getText());
        double inverso = 1/numero;
        ResPantalla.setText(Double.toString(inverso));
        
    }//GEN-LAST:event_BotonInvertirActionPerformed
   
    public static boolean existePunto(String cadena){
        boolean resultado = false;
        for(int i=0; i<cadena.length(); i++){
            if(cadena.charAt(i) == '.'){
                resultado = true;
            }
        }
        return resultado;
    }

    public static boolean esNumNegativo(String cadena){
        boolean esNegativo = false;
        if(cadena.charAt(0) == '-'){
            esNegativo = true;
        }
        return esNegativo;
    }
    
    public static double calcular(String signo, String num1, String num2){
        double resultado = 0;
            switch(signo){
                case "+":
                    resultado = Double.valueOf(num1) + Double.valueOf(num2);
                    break;
                case "-":
                    resultado = Double.valueOf(num1) - Double.valueOf(num2);
                    break;
                case "x":
                    resultado = Double.valueOf(num1) * Double.valueOf(num2);
                    break;
                case ":":
                    resultado = Double.valueOf(num1) / Double.valueOf(num2);
            }
        return resultado;
    }
    
